const socket = io('http://your-socketio-server-url');

class SocketIOFile {
  constructor() {
    this.filesToSend = [];
    this.filesReceived = [];

    socket.on('connect', () => {
      console.log('Connected to server');
    });

    socket.on('disconnect', () => {
      console.log('Disconnected from server');
    });

    socket.on('file_transfer', (json_data) => {
      this.receiveFile(json_data);
    });
  }

  convertToBase64(file) {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onloadend = () => {
        resolve(reader.result.split(',')[1]);
      };
      reader.onerror = reject;
      reader.readAsDataURL(file);
    });
  }

  calculateSHA256(file) {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onloadend = () => {
        const binaryData = new Uint8Array(reader.result);
        crypto.subtle.digest('SHA-256', binaryData)
          .then(hashBuffer => {
            const hashArray = Array.from(new Uint8Array(hashBuffer));
            const hashHex = hashArray.map(byte => byte.toString(16).padStart(2, '0')).join('');
            resolve(hashHex);
          })
          .catch(reject);
      };
      reader.onerror = reject;
      reader.readAsArrayBuffer(file);
    });
  }

  sendFile(file, metadata) {
    const base64Promise = this.convertToBase64(file);
    const sha256Promise = this.calculateSHA256(file);

    Promise.all([base64Promise, sha256Promise])
      .then(([base64Data, sha256Hash]) => {
        metadata.sha256 = sha256Hash;
        metadata.filename = file.name;

        const data = {
          metadata: metadata,
          file: base64Data
        };
        const json_data = JSON.stringify(data);
        socket.emit('file_transfer', json_data);

        this.filesToSend.push(file.name);
      })
      .catch(error => {
        console.error('Error occurred while sending the file:', error);
      });
  }

  saveFile(base64Data, receivedFilename) {
    const byteCharacters = atob(base64Data);
    const byteNumbers = new Array(byteCharacters.length);
    for (let i = 0; i < byteCharacters.length; i++) {
      byteNumbers[i] = byteCharacters.charCodeAt(i);
    }
    const byteArray = new Uint8Array(byteNumbers);
    const blob = new Blob([byteArray], { type: 'application/octet-stream' });

    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = receivedFilename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  }

  receiveFile(jsonData) {
    const data = JSON.parse(jsonData);
    const metadata = data.metadata;
    const base64Data = data.file;
    const receivedFilename = metadata.filename;
    this.saveFile(base64Data, receivedFilename);

    console.log('File received and saved:', receivedFilename);
    this.filesReceived.push(receivedFilename);
  }

  connect() {
    socket.connect();
  }

  disconnect() {
    socket.disconnect();
  }
}

