```csharp
private IEnumerator SendRequestToServer()
{
    // Create a JSON object containing the necessary data
    JSONObject requestData = new JSONObject();
    requestData.AddField("prompt", prompt);

    // Create a UnityWebRequest object to send the request
    UnityWebRequest request = UnityWebRequest.Post(serverURL, requestData.ToString());
    request.SetRequestHeader("Content-Type", "application/json");
    request.SetRequestHeader("Authorization", $"Bearer {apiKey}");

    // Send the request and wait for the response
    yield return request.SendWebRequest();

    // Check if there are any errors
    if (request.result != UnityWebRequest.Result.Success)
    {
        Debug.LogError("Error sending request to server: " + request.error);
    }
    else
    {
        // Parse the response
        JSONObject response = new JSONObject(request.downloadHandler.text);
        string generatedText = response["generatedText"].str;

        // Do something with the generated text
        Debug.Log("Generated Text: " + generatedText);
    }
}
```

