<script>
import Prism from 'prismjs';

export default {
  data() {
    return {
      renderedMarkdown: '',
      isCopied: false,
    };
  },
  props: {
    markdown: {
      type: String,
      required: true,
    },
  },
  mounted() {
    this.renderMarkdown();
  },
  methods: {
    renderMarkdown() {
      // Process the markdown using a markdown library of your choice
      // For example, using markdown-it library
      const markdown = require('markdown-it')();
      const processedMarkdown = markdown.render(this.markdown);

      // Apply syntax highlighting to code blocks using Prism.js
      const div = document.createElement('div');
      div.innerHTML = processedMarkdown;
      const codeBlocks = div.querySelectorAll('code');
      codeBlocks.forEach((codeBlock) => {
        Prism.highlightElement(codeBlock);
      });

      this.renderedMarkdown = div.innerHTML;
    },
    copyCode(event) {
      const code = event.target.innerText;
      // Perform the copy operation using Clipboard API or a custom implementation
      // For brevity, we'll assume a custom implementation here
      this.copyToClipboard(code);
      this.isCopied = true;

      setTimeout(() => {
        this.isCopied = false;
      }, 1500);
    },
    copyToClipboard(text) {
      const textarea = document.createElement('textarea');
      textarea.value = text;
      document.body.appendChild(textarea);
      textarea.select();
      document.execCommand('copy');
      document.body.removeChild(textarea);
    },
  },
};
</script>

<style>
