    renderedMarkdown() {
      const md = new MarkdownIt();

      // Customize the renderer to handle code tags and images
      md.renderer.rules.fence = (tokens, idx) => {
        const token = tokens[idx];
        const code = token.content.trim();
        const lang = token.info || '';

        if (lang && lang !== 'text') {
          try {
            const highlightedCode = hljs.highlight(lang, code).value;
            return `<pre><code class="hljs ${lang}">${highlightedCode}</code></pre>`;
          } catch (e) {
            console.error(`Syntax highlighting failed for language '${lang}':`, e);
          }
        }

        return `<pre><code>${md.utils.escapeHtml(code)}</code></pre>`;
      };

      md.renderer.rules.image = (tokens, idx) => {
        const token = tokens[idx];
        const src = md.utils.escapeHtml(token.attrs[token.attrIndex('src')][1]);
        const alt = token.content ? md.utils.escapeHtml(token.content) : '';
        const title = token.attrs[token.attrIndex('title')][1];

        return `<img src="${src}" alt="${alt}" title="${title}" />`;
      };

      // Allow embedding of HTML
      md.set({ html: true });

      return md.render(this.markdown);
    },
  },
};
</script>

