    return {
      renderedMarkdown: ''
    };
  },
  mounted() {
    this.renderedMarkdown = markdownIt.render(this.markdownText);
    nextTick(() => {
      feather.replace();
      this.addCopyButtons();
    });
  },
  methods: {
    addCopyButtons() {
      const codeBlocks = this.$el.querySelectorAll('code.hljs');
      codeBlocks.forEach(codeBlock => {
        const copyButton = document.createElement('button');
        copyButton.classList.add(
          'copy-button',
          'absolute',
          'right-2',
          'top-2',
          'px-2',
          'py-1',
          'bg-primary',
          'hover:bg-primary-dark',
          'active:bg-primary-darker',
          'rounded',
          'text-white',
          'text-xs',
          'transition-colors',
          'duration-200'
        );
        copyButton.innerHTML = `
          <span class="mr-1">Copy</span>
          <span class="hidden copied-text text-xs text-green-500">Copied!</span>
          <svg xmlns="http://www.w3.org/2000/svg" class="feather feather-copy" width="12" height="12" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
            <path d="M5 20h14a2 2 0 0 0 2-2V7a2 2 0 0 0-2-2H5a2 2 0 0 0-2 2v11a2 2 0 0 0 2 2zM9 2h6v4H9zm5 8H9v4h5zm0-8v4h4V2z"></path>
          </svg>
        `;
        codeBlock.parentNode.insertBefore(copy

Button, codeBlock.nextSibling);
        copyButton.addEventListener('click', () => {
          const codeContent = codeBlock.textContent;
          this.copyContentToClipboard(codeContent);
          copyButton.classList.add('copied');
          const copiedText = copyButton.querySelector('.copied-text');
          copiedText.classList.remove('hidden');
          setTimeout(() => {
            copyButton.classList.remove('copied');
            copiedText.classList.add('hidden');
          }, 1500);
        });
      });
    },
    copyContentToClipboard(content) {
      navigator.clipboard.writeText(content);
    }
  },
  watch: {
    markdownText(newText) {
      this.renderedMarkdown = markdownIt.render(newText);
      nextTick(() => {
        feather.replace();
        this.addCopyButtons();
      });
    }
  }
};
</script>

